    private void maxbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_maxbtnActionPerformed

        //Max calc code
        //Inputs
        double maxValue1;
        maxValue1 = Double.parseDouble(maxtxt1.getText());
        double maxValue2;
        maxValue2 = Double.parseDouble(maxtxt2.getText());
        
        //Calculation
        double maxResult = Math.max(maxValue1, maxValue2);
        
        //Output
        maxoutputlbl.setText(String.valueOf(maxResult));
    }//GEN-LAST:event_maxbtnActionPerformed

