    private void minbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_minbtnActionPerformed
        //Min calc code
        //Inputs
        double minValue1;
        minValue1 = Double.parseDouble(mintxt1.getText());
        double minValue2;
        minValue2 = Double.parseDouble(mintxt2.getText());
        
        //Calculation
        double minResult = Math.min(minValue1, minValue2);
        
        //Output
        minoutputlbl.setText(String.valueOf(minResult));
    }//GEN-LAST:event_minbtnActionPerformed

    private void powbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_powbtnActionPerformed
        //pow calc code
        DecimalFormat x = new DecimalFormat("###.##");
        //Inputs
        double powValue1;
        powValue1 = Double.parseDouble(powtxt1.getText());
        double powValue2;
        powValue2 = Double.parseDouble(powtxt2.getText());
        
        //Calculation
        double powResult = Math.pow(powValue1, powValue2);
        
        //Output
        powoutputlbl.setText(String.valueOf(x.format(powResult)));
    }//GEN-LAST:event_powbtnActionPerformed

