    private void floorbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_floorbtnActionPerformed
        //floor calc code
        //Inputs
        double floorValue1;
        floorValue1 = Double.parseDouble(floortxt.getText());
        
        //Calculation
        double floorResult = Math.floor(floorValue1);
        
        //Output
        flooroutputlbl.setText(String.valueOf(floorResult));
    }//GEN-LAST:event_floorbtnActionPerformed

